//output: c = m^2e mod n
void creat_c_new(mpz_t c){
	mpz_t x,n;
	mpz_inits(x,n,NULL);
	mpz_set_str(x,c_star_s,10); //x = c_star = m^e mod n
	mpz_set_str(n,N_str,10);
	
	mpz_powm_ui(c,x,2,n);
	
}

